import React from "react";
const Header = (props) => {
  // console.log(props);
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
};
const Part = (props) => {
  console.log(props);
  return (
    <p>
      {props.part} {props.exercises}
    </p>
  );
};
// const Content = (props) => {
